import { createSlice } from "@reduxjs/toolkit";
import CardModel from "../../models/card.model";

export const initialState = {
  card: {} as CardModel,
};

export const cardsSlice = createSlice({
  name: "card",
  initialState,
  reducers: {
    storeGetCardById: (state, action) => {
      state.card = action.payload;
    },
    storeDeleteCard: (state) => {
      state.card = {} as CardModel;
    },
    editCardById: (state, action) => {
      state.card = action.payload;
    },
  },
});

// Actions generated by the slice
export const { storeGetCardById, storeDeleteCard, editCardById } =
  cardsSlice.actions;

//Selectors
export const cardSelector = (state: { card: { card: any } }) => state.card.card;

// reducer
export default cardsSlice.reducer;
